import React, { useState } from "react";
import "./DishModal.css";
import Card from "../Card/Card";
import { ICard, IModal } from "../../interFaces/interFaces";
import { useSelector } from "react-redux";
import { IRootState } from "../../store/store/store";

const DishModal: React.FC<IModal> = (props: IModal) => {
  const [dishName, setDishName] = useState("");
  const specificDishes = useSelector((state: IRootState) => state.dishes.value);
  const clickedDish = specificDishes.find((dish) => dish.id === props.dishId);
  return (
    <>
      <div>
        <Card
          name={clickedDish?.name}
          about={clickedDish?.about}
          icons={clickedDish?.icons}
          price = {clickedDish}
        />
        <div>
          <div>
            <h1>Choose a side</h1>
            <form action="">
              <label htmlFor="">
                <input type="radio" />
              </label>
            </form>
          </div>
          <div>
            <h1>Changes</h1>
            <form action="">
              <label htmlFor="">
                <input type="checkbox" />
              </label>
            </form>
          </div>
          <div>
            <h1>Quantity</h1>
            <button>-</button>
            <p>2</p>
            <button>+</button>
          </div>
          <button>add to cart</button>
        </div>
      </div>
    </>
  );
};

export default DishModal;
