    <>
      <div id="restaurant">
        {filteredRest.map((card: ICard, index: number) => (
            <Card

              class={card.price ? "dish" : "rest"}
              key={index}
              img={card.img}
              name={card.name}
              icons={card.icons}
              about={card.about}
              price={card.price}
              chef={card.chef}
              rating={card.rating}
            />
        ))}
      </div>
    </>
  );
};

