import { createSlice } from "@reduxjs/toolkit";
import { stat } from "fs";
import data from "../../epicure.json";

export const dishesSlicer = createSlice({
  name: "dishes",
  initialState: {
    value: data.dishes,
    default : data.dishes
  },
  reducers: {
    dishTimeFilter: (state,action) => {
      switch (action.payload) {
        case "Breakfast":
          specificDishes = stat.filter((dish) =>
            dish.time.find((time) => time === "breakfast")
          );
          break;
        case "Lunch":
          specificDishes = AllRestDishes.filter((dish) =>
            dish.time.find((time) => time === "lunch")
          );
          break;
        case "Dinner":
          specificDishes = AllRestDishes.filter((dish) =>
            dish.time.find((time) => time === "dinner")
          );
          break;
        default:
          specificDishes = AllRestDishes;
          break;
      }
    }
  },
});

export default dishesSlicer.reducer;
