import { useState } from "react";
import { IButtons } from "../../../interFaces/interFaces";
import DistanceButton from "../DistanceButton/DistanceButtons";
import { PriceButton } from "../PriceButtons/PriceButtons";
import RatingButton from "../RatingButton/Ratingbutton";

const FilterContainer: React.FC<IButtons> = (props):any =>{
 const [addPrice, setAddPrice]:any = useState(false)
 const [addDistance, setAddDistance]:any = useState(false)
 const [addRating, setAddRating]:any = useState(false)
    const openRang = (x:any):any=>{
      x(true)
    }
    return (
      <>
        <button id="price" className="open-sort" onClick={()=>openRang(setAddPrice)}><h1>Price Range</h1><img src="/image/icons/down arrow.svg" alt="down arrow" /></button>
        {addPrice && <PriceButton name="Price Range"/>}
        <button id="distance" className="open-sort" onClick={()=>openRang(addDistance)}><h1>Distance</h1><img src="/image/icons/down arrow.svg" alt="down arrow" /></button>
        {/* <DistanceButton name="Distance" /> */}
        <button id="rating" className="open-sort" onClick={()=>openRang(addRating)}><h1>Rating</h1><img src="/image/icons/down arrow.svg" alt="down arrow" /></button>
        <RatingButton name={"Rating"} />
      </>
    );

}
   

  export default FilterContainer;
