import React from "react";
import { IButtons } from "../../interFaces/interFaces";
// import "./Buttons.css";
import styled from "styled-components";

export const SimpleButtons = styled.button`
  font-family: "Helvetica";
  font-size
  cursor: pointer;
  border-radius: 6px;
  border: none;
  font-weight: bolder;
  &:hover {
    font-weight: bolder;
  }
  &:focus {
    font-weight: bold;
  }
`;

const FilterButtons: any = (props: IButtons) => {
  const filter = (e: any | null) => {
    const btnName = e.target.id;
    console.log(btnName);
  };
  return (
    <>
      <SimpleButtons id={props.name} onClick={filter}>
        {props.name}
      </SimpleButtons>
    </>
  );
};

export default FilterButtons;
