import React from "react";
import { IButtons } from "../../interFaces/interFaces";
// import "./Buttons.css";
import styled from "styled-components";

export const SimpleButtons = styled.button`
  font-family: "Helvetica";
  font-size: 1.2vw;
  cursor: pointer;
  border-radius: 6px;
  border: none;
  font-weight:
  &:hover {
    font-weight: bolder;
  }
  &:focus {
    font-weight: bolder;
  }
`;
export const SortButtons = styled.select`
  font-family: "Helvetica";
  font-size: 1.2vw;
  cursor: pointer;
  border-radius: 6px;
  border: none;
  height: 4vw;
  width: 13vw;
  text-align: center;
  &:hover {
    font-weight: bolder;
  }
  &:hover {
    background-color: rgba(208, 207, 207, 1);
  }
  &:focus {
    font-weight: bolder;
    background-color: rgba(208, 207, 207, 1);
  }
`;

export const FilterButtons: any = (props: IButtons) => {
  const filter = (e: any | null) => {
    const btnName = e.target.id;
    console.log(btnName);
    return btnName;
  };
  return (
    <>
      <SimpleButtons id={props.name} onClick={filter}>
        {props.name}
      </SimpleButtons>
    </>
  );
};

export const RangeButtons: any = (props: IButtons) => {
  const filter = (e: any | null) => {
    const btnName = e.target.id;
    console.log(btnName);
    return btnName;
  };
  return (
    <>
      <SortButtons id={props.name} onClick={filter}>{props.name}
      <option value="">{props.name}</option>
      <option value=""><input type="range" /></option>
      </SortButtons>
      
    </>
  );
};
