import { createSlice } from "@reduxjs/toolkit";
import { stat } from "fs";
import { useSelector } from "react-redux";
import data from "../../epicure.json";
import { IRootState } from "../store/store";
import { restaurantSlice } from "./restaurantSlicer";

export const dishesSlicer = createSlice({
  name: "dishes",
  initialState: {
    value: data.dishes,
    default : data.dishes,
    specificDishes: []
  },
  reducers: {
    getDishesByRestId: (state,action) => {
      const restArray = useSelector((state: IRootState) => state.restaurants.value);
      state.specificDishes = state.default.filter((dish) => dish.restaurantId === restArray.find((rest) => rest.name === action.payload) )

    },
    dishTimeFilter: (state,action) => {
      switch (action.payload) {
        case "Breakfast":
          state.value = state.default.filter((dish) =>
            dish.time.find((time) => time === "breakfast")
            );
            console.log("1")
          break;
        case "Lunch":
          state.value = state.default.filter((dish) =>
            dish.time.find((time) => time === "lunch")
          );
          console.log("12")

          break;
        case "Dinner":
          state.value = state.default.filter((dish) =>
            dish.time.find((time) => time === "dinner")
          );
          console.log("3")

          break;
        default:
          state.value = state.default;
          break;
      }
    }
  },
});

export const {dishTimeFilter} = dishesSlicer.actions
export default dishesSlicer.reducer;
