import { IButtons } from "../../interFaces/interFaces";
// import "./Buttons.css";
import styled from "styled-components"

export const SimpleButtons = styled.button`
 cursor: pointer;
 font-weight: 400;
 border-radius: 6px;
 border: none;
 &:hover {
   background-color: #4dd9f8;
 }
 &:focus {
   font-weight: bold;
 }
`;


const FilterButtons: any = (props:IButtons) => {
    const filter = (e:any | null)=>{
        const btnName = e.target.id
        console.log(btnName)
    }
      return (<>
      <SimpleButtons id={props.name} onClick={filter}>{props.name}</SimpleButtons>
        </>
      )
    }

export default FilterButtons;
