import React from "react";
import data from '../../../epicure.json'
import { ICard, IRenderRest } from "../../../interFaces/interFaces";
import Card from "../../Card/Card";
// import "./RenderRest.css";


const RenderRest: React.FC<{}> = (props:IRenderRest) => {
    const filteredRest = data.restaurant.filter(e =>{
        switch (props.sortFilter) {
            case 'All':
                filteredRest === data.restaurant
                break;
            case 'New':
                filteredRest === data.restaurant
                break;
            case 'Open Now':
                const d = new Date()
                const hour = d.getHours
                const day = d.getDay
                for (let i = 0;i < data.restaurant.length; )
                if ((data.restaurant.openHours[0] <= hour && hour < e.openHours[1]) && (e.openDays.includes(day)))
                filteredRest === data.restaurant
                break;
            case 'Most Popular':
                filteredRest === data.restaurant
                break;
            case 'Map View':
                filteredRest === data.restaurant
                break;
        
            default:
                break;
        }
    }  )
      return (<>
      <div>
        {filteredRest.map((card: ICard,index:number) =>(
            <Card class={card.price ? "dish" : "rest"} key={index} img={card.img} name={card.name} icons={card.icons} about={card.about} price={card.price}   chef={card.chef} rating= {card.rating} />
        ))}
      </div>

        </>
      )
    }

export default RenderRest;
