import React, { useState, useEffect } from "react";
import "./Popular.css";
import data from "../../epicure.json";
import SingleRest from "../SingleRest/SingleRest";
import Restaurant from "../Restaurant/Restaurant";

const Popular: React.FC = () => {
  const [popular, setPopular] = useState<any>([]);

  useEffect(() => {
    if (data)
    setPopular(data.restaurant.filter((rest) => rest.rating.includes("4")));
    console.log(data.restaurant);
  }, []);

  return (
    <>
      <div id="popular">
        <h1>popular restaurant in epicure:</h1>
        <div id="three-div">
          {popular.map((rest: object, index: number) => {
            index < 3 && <SingleRest restaurant={rest} />;
          })}
        </div>
        <button>
          All restaurants <img src="/image/vector.svg" alt="" />
        </button>
      </div>
    </>
  );
};

export default Popular;
