import React from "react";
import "./SingleRest.css";
import { useSelector } from "react-redux";
import { IRootState } from "../../../store/store/store";
import Card from "../../Card/Card";


const SingleRest: React.FC<any> = (props) => {
    const restArray = useSelector(
        (state:IRootState) => state.restaurants.value
      );
    const dishArray = useSelector(
        (state:IRootState) => state.dishes.value
      );
      let restIndex = restArray.findIndex((e:any) => e.name === props.restName)
      let d=new Date();
      return (<>
      <Card
              class={"rest"}
              img={restArray[restIndex].img}
              name={restArray[restIndex].name}
              chef={restArray[restIndex].chef}
            />
            {(restArray[restIndex].openDays.findIndex((e)=> e===d.getDay()) ? <img src="/image/icons/open now.svg" /> : null)}
        </>
      )
    }

export default SingleRest;
